def astype_nansafe(arr, dtype, copy: bool = True, skipna: bool = False):
    # if input dtype is the same as arr.dtype, return the array as it is
    if dtype == arr.dtype:
        return arr

    # handle NaT values
    if np.any(pd.isna(arr)):
        raise ValueError("Cannot convert NaT values to integer")

    # dispatch on extension dtype if needed
    if is_extension_array_dtype(dtype):
        return dtype.construct_array_type()._from_sequence(arr, dtype=dtype, copy=copy)

    if not isinstance(dtype, np.dtype):
        dtype = np.dtype(dtype)

    if issubclass(dtype.type, str):
        return lib.astype_str(arr.ravel(), skipna=skipna).reshape(arr.shape)

    elif is_datetime64_dtype(arr):
        if is_object_dtype(dtype):
            return pd.to_datetime(arr).view(dtype)
        elif dtype == np.int64:
            return arr.view(dtype)
        elif dtype.kind == 'M':
            return arr.astype(dtype)
        else:
            raise TypeError(f"cannot astype a datetimelike from [{arr.dtype}] to [{dtype}]")

    elif is_timedelta64_dtype(arr):
        if is_object_dtype(dtype):
            return pd.to_timedelta(arr).view(dtype)
        elif dtype == np.int64:
            return arr.view(dtype)
        elif dtype.kind == 'm':
            mask = pd.isna(arr)
            result = arr.astype(dtype).astype(np.float64)
            result[mask] = np.nan
            return result
        elif dtype == _TD_DTYPE:
            return arr.astype(_TD_DTYPE, copy=copy)
        else:
            raise TypeError(f"cannot astype a timedelta from [{arr.dtype}] to [{dtype}]")

    elif np.issubdtype(arr.dtype, np.floating) and np.issubdtype(dtype, np.integer):
        if not np.isfinite(arr).all():
            raise ValueError("Cannot convert non-finite values (NA or inf) to integer")

    elif is_object_dtype(arr):
        if np.issubdtype(dtype.type, np.integer):
            return lib.astype_intsafe(arr.ravel(), dtype).reshape(arr.shape)
        elif is_datetime64_dtype(dtype):
            return astype_nansafe(pd.to_datetime(arr).values, dtype, copy=copy)
        elif is_timedelta64_dtype(dtype):
            return astype_nansafe(pd.to_timedelta(arr).values, dtype, copy=copy)

    if dtype.name in ('datetime64', 'timedelta64'):
        msg = (f"The '{dtype.name}' dtype has no unit. Please pass in "
               f"'{dtype.name}[ns]' instead.")
        raise ValueError(msg)

    if copy or is_object_dtype(arr) or is_object_dtype(dtype):
        # Explicit copy, or required since NumPy can't view from / to object.
        return arr.astype(dtype, copy=True)

    return arr.view(dtype)
