def apply(self, other):
    if isinstance(other, datetime.datetime):
        nanosecond = getattr(other, "nanosecond", 0)
        other = datetime.datetime(
            other.year,
            other.month,
            other.day,
            other.hour,
            other.minute,
            other.second,
            other.microsecond,
        )
        n = self.n

        # fixing names here
        if n >= 0:
            if other.time() in self.end or not is_on_offset(other):
                other = _next_opening_time(other)
        else:
            if other.time() in self.start:
                other = other - timedelta(seconds=1)
            if not is_on_offset(other):
                other = _next_opening_time(other)
                other = _get_closing_time(other)

        # handling of BusinessDay class
        businesshours = sum(
            _get_business_hours_by_sec(st, en)
            for st, en in zip(self.start, self.end)
        )

        bd, r = divmod(abs(n * 60), businesshours // 60)
        if n < 0:
            bd, r = -bd, -r

        if bd != 0:
            skip_bd = BusinessDay(n=bd)
            if not skip_bd.is_on_offset(other):
                prev_open = _prev_opening_time(other)
                remain = other - prev_open
                other = prev_open + skip_bd + remain
            else:
                other = other + skip_bd

        bhour_remain = timedelta(minutes=r)

        if n >= 0:
            while bhour_remain != timedelta(0):
                bhour = (
                    _get_closing_time(_prev_opening_time(other)) - other
                )
                if bhour_remain < bhour:
                    other += bhour_remain
                    bhour_remain = timedelta(0)
                else:
                    bhour_remain -= bhour
                    other = _next_opening_time(other + bhour)
        else:
            while bhour_remain != timedelta(0):
                bhour = _next_opening_time(other) - other
                if (
                    bhour_remain > bhour
                    or bhour_remain == bhour
                    and nanosecond != 0
                ):
                    other += bhour_remain
                    bhour_remain = timedelta(0)
                else:
                    bhour_remain -= bhour
                    other = _get_closing_time(
                        _next_opening_time(
                            other + bhour - timedelta(seconds=1)
                        )
                    )

        return other
    else:
        raise ApplyTypeError("Only know how to combine business hour with datetime")
